package week2_1;

public class WrapperMinMaxValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int imin = Integer.MIN_VALUE, imax = Integer.MAX_VALUE;
		double dmin = Double.MIN_VALUE, dmax = Double.MAX_VALUE;
		Long lmin = Long.MIN_VALUE, lmax = Long.MAX_VALUE;
		short smin = Short.MIN_VALUE, smax = Short.MAX_VALUE;
		byte bmin = Byte.MIN_VALUE, bmax = Byte.MAX_VALUE;
		float fmin = Float.MIN_VALUE, fmax = Float.MAX_VALUE;
		System.out.println("Integer Range:\nmin: "+imin+"\nmax: "+imax);
		System.out.println("Double Range:\nmin: "+dmin+"\nmax: "+dmax);
		System.out.println("Long Range:\nmin: "+lmin+"\nmax: "+lmax);
		System.out.println("Short Range:\nmin: "+smin+"\nmax: "+smax);
		System.out.println("Byte Range:\nmin: "+bmin+"\nmax: "+bmax);
		System.out.println("Float Range:\nmin: "+fmin+"\nmax: "+fmax);
		
	}

}
